/**
 * Course Reducer
 * @exports {state} reducer
 * @param {object} action
 * @return {state} state
 */
export default function reducer(state={
    courseList: null,
    currentVendorCourseList: null,
    fetching: false,
    error: null,
    aCourse: "Loading...",
    aCourseName:"Loading...",
    uploaded: false,

  }, action) {

    switch (action.type) {

        case "FETCH_A_COURSE":{
            return {...state, 
                    fetching: true
            }
        }
        case "FETCH_A_COURSE_FULFILLED":{
            return {...state, 
                    fetching: false,
                    aCourse: action.payload,
            }
        }
        case "FETCH_A_COURSE_REJECTED":{
            return {...state, 
                    fetching:false,
                    error: action.payload,
            }
        }

        case "FETCH_COURSE": {
            return {...state, 
                    fetching: true
            }
        }
        case "FETCH_COURSE_FULFILLED": {
            return{...state, 
                    fetching: false,
                    courseList: action.payload,
            }
        }
        case "FETCH_COURSE_REJECTED": {
            return{...state, 
                    fetching: false,
                    error: action.payload,
            }
        }
        case "FETCH_VENDORS_COURSE": {
            return {...state, 
                    fetching: true
            }
        }

        case "FETCH_VENDORS_COURSE_FULFILLED": {
            return {...state, 
                    fetching: false,
                    currentVendorCourseList: action.payload
            }
        }
        case "FETCH_VENDORS_COURSE_REJECTED": {
            return {...state, 
                    fetching: false,
                    error: action.payload
            }
        }

        case "UPLOAD_COURSE": {
            return {...state, 
                    uploaded: false,
                    fetching: true
            }
        }

        case "UPLOAD_COURSE_FULFILLED": {
            return {...state,
                    uploaded: true,
                    fetching: false,
            }
        }
        case "UPLOAD_COURSE_REJECTED": {
            return {...state, 
                    fetching: false,
            }
        }

        case "SAVE_A_COURSE": {
            return {...state,
                    aCourseName : action.payload
            }
        }

    }

    return state
}

/**
 * Profile Reducer
 * @exports {state} reducer
 * @param {object} action
 * @return {state} state
 */

export default function reducer(state={
    menu: 0,

  }, action) {

    switch (action.type) {
        case "CHANGE_MENU": {
            return {...state, 
               menu: action.payload }
        }    
    }

    return state
}

/**
 * User Reducer
 * @exports {state} reducer
 * @param {object} action
 * @return {state} state
 */
export default function reducer(state={
    user: null,
    userName:null,
    fetching: false,
    fetched: false,
    error: null,
    isLoggedIn:false,
    role: null,
    purchasers: null,
    vendors: null,
    ad: null,
    profile:{
        role:null,
        firstName: null,
        // *TODO*
        // add more attribute here in model
        // null as default, i copie role into outer scope for convenience :)
    },
  }, action) {

    switch (action.type) {

        case "FETCH_USER": {
            return {...state,
                fetching: true}
        }

        case "FETCH_USER_REJECTED": {
            return {
                ...state,
                fetching: false,
                error: action.payload
            }
        }

        case "FETCH_USER_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched: true,
                user: action.payload,
                isLoggedIn: action.isLoggedIn,
            }
        }

        case "FETCH_PURCHASER_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched: true,
                purchasers: action.payload,
                isLoggedIn: action.isLoggedIn,
            }
        }

        case "FETCH_VENDOR_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched: true,
                vendors: action.payload,
                isLoggedIn: action.isLoggedIn,
            }
        }

        case "FETCH_AD_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched: true,
                ad: action.payload,
                isLoggedIn: action.isLoggedIn,
            }
        }

        case "SIGNUP_USER": {
            return {
                ...state,
                fetching: true,
            }
        }

        case "SIGNUP_USER_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched:true,
            }
        }

        case "SIGNUP_USER_REJECTED": {
            return {
                ...state,
                fetching: false,
                error: action.payload
            }
        }

        case "UPDATE_USER_PROFILE": {
            return {
                ...state,
                fetching: true,
            }
        }

        case "UPDATE_USER_PROFILE_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched:true,
                profile:action.payload,
                role: action.payload.role,
            }
        }

        case "UPDATE_USER_PROFILE_REJECTED": {
            return {
                ...state,
                fetching: false,
                error: action.payload
            }
        }

        case "FETCH_USER_PROFILE": {
            return {
                ...state,
                fetching: true,
            }
        }

        case "FETCH_USER_PROFILE_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched:true,
                isLoggedIn:true,
                profile:action.payload,
                role: action.payload.role,
            }
        }

        case "FETCH_USER_PROFILE_REJECTED": {
            return {
                ...state,
                fetching: false,
                error: action.payload
            }
        }

        case "LOGIN_USER": {
            return {
                ...state,
                fetching: true,
            }
        }

        case "LOGIN_USER_FULFILLED": {
            return {
                ...state,
                fetching: false,
                fetched:true,
                user: action.payload,
                userName: action.payload.email,
                isLoggedIn:true,
            }
        }

        case "LOGIN_USER_REJECTED": {
            return {
                ...state,
                fetching: false,
                isLoggedIn: false,
                error: action.payload
            }
        }

        case "LOGOUT_USER": {
            return {
                ...state,
                fetching: true,
                fetched: false,
            }
        }

        case "LOGOUT_USER_FULFILLED": {
            return{
                ...state,
                fetching: false,
                fetched: true,
                user: null,
                role: null,
                isLoggedIn: false,
                purchasers: null,
                vendors: null,
                ad: null,
            }
        }

        case "LOGOUT_USER_REJECTED": {
            return {
                ...state,
                fetching: false,
                isLoggedIn: true,
                error: action.payload
            }
        }

    }

    return state
}

