import "../../extra/material.js"
import { uploadCourse } from "../Actions/courseActions"
import { addTag,deleteTag } from "../Actions/tagActions"
import Toast from "../Toast.js"

var componentStyle = {
    margin: 'auto',
}
var formStyle = {
    marginTop: '5%'
}


@connect((store) => {
  return {
